mapper.update:
UPDATE MENTEE_APPLY
SET apply_status = 'ACCEPTED'
WHERE mentor_member_id = #{mentorMemberId}
  AND mentee_member_id = #{menteeMemberId}
  AND apply_status = 'REQUESTED'

UPDATE MENTEE_APPLY
SET apply_status = 'REJECTED'
WHERE mentor_member_id = #{mentorMemberId}
  AND mentee_member_id = #{menteeMemberId}
  AND apply_status = 'REQUESTED'
