mapper.insert:
INSERT INTO MENTOR(
    MENTOR_MEMBER_ID,
    MENTOR_WANT_TO_SAY,
    MENTOR_DISCORD_ID
    )
VALUES(
    #{mentorMemberId},
    #{mentorWantToSay},
    #{mentorDiscordId}
)

INSERT INTO MENTOR_CONTENT (
MENTOR_MEMBER_ID,
MENTOR_CONTENT_ID
)
VALUES (
#{mentorMemberId},
#{mentorContentId}
)

mapper.select:
SELECT mentor_discord_id
FROM MENTOR
WHERE mentor_member_id = #{mentorMemberId}

SELECT COUNT(*)
FROM MENTEE_APPLY
WHERE mentor_member_id = #{mentorMemberId}
AND mentee_member_id = #{menteeMemberId}

SELECT mentor_member_id 
FROM MENTEE_APPLY 
WHERE mentee_member_id = #{menteeId}
